import React, { useState } from "react";
import AvailableMeals from "./AvailableMeals";
import CategoriesMeals from "./CategoriesMeals";
import MealsSummary from "./MealsSummary";
import Parrillada from "./Parrillada";



const Meals = () => {
  const [meals, setMeals] = useState(0);

  const addParrillaHandler = () => {
    setMeals(2);
  }

  const addMealsHandler = () => {
    setMeals(1);
  }

  const addGoBackHandler = () => {
    setMeals(0);
  }

  return (
    <React.Fragment>
      <MealsSummary />
      {meals === 0 && <CategoriesMeals onAddMeal={addMealsHandler}/>}
      {meals === 1 && <AvailableMeals onGoBack={addGoBackHandler}/>}
      {meals === 2 && <Parrillada onAddParrilla={addParrillaHandler}/>}
    </React.Fragment>
  );
};

export default Meals;
