import React, { useEffect, useState, useContext } from "react";
import Swal from "sweetalert2";

import classes from "./Orders.module.css";
import Card from "../../UI/Card";

const Orders = (props) => {
  const [order, setOrder] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      const response = await fetch(
        "https://menu-app-d307a-default-rtdb.firebaseio.com/orders.json"
      );

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseData = await response.json();

      const loadedOrders = [];

      for (const key in responseData) {
        // console.log(responseData[key].orderedItems)
        loadedOrders.push({
          id: key,
          orderedItems: responseData[key].orderedItems,
          user: responseData[key].user,
        });
      }
      setOrder(loadedOrders);
      setIsLoading(false);
      console.log(loadedOrders);
    };

    fetchOrders().catch((error) => {
      setIsLoading(false);
      setError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section className={classes.mealsLoading}>
        <p>Loading...</p>
      </section>
    );
  }

  if (error) {
    return (
      <section className={classes.mealsLoading}>
        <p>
          {Swal.fire({
            icon: "error",
            title: "Oops...",
            text: error,
          })}
        </p>
      </section>
    );
  }

  const totalAmount = order.map((item) => (
    <p>
      {item.orderedItems.map((total) => (
        <p>{total.amount}</p>
      ))}
    </p>
  ));

  console.log(totalAmount);

  const mealsList = order.map((orders) => (
    <li>
      {orders.user.name}
      {orders.orderedItems.map((item) => (
        <p>
          {item.amount} -- {item.name} -- {item.amount * item.price}
        </p>
      ))}
      <span>Total: {} </span>
    </li>
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
        <button className={classes.button} onClick={props.onGoBack}>
          Atr√°s
        </button>
      </Card>
    </section>
  );
};

export default Orders;
