import React, { useEffect, useState } from "react";
import Swal from "sweetalert2";

import classes from "./Orders.module.css";
import MealItem from "./MealItem";
import Card from "../../UI/Card";

const Orders = (props) => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        "https://menu-app-d307a-default-rtdb.firebaseio.com/orders.json"
      );

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseData = await response.json();

      console.log(responseData)

      const loadedOrders = [];

      for (const key in responseData) {
        loadedOrders.push({
          id: key,
          orderedItems: responseData[key].orderedItems,
          user: responseData[key].user
        });
      }
      setMeals(loadedOrders);
      setIsLoading(false);
      console.log(loadedOrders)
    };

    fetchMeals().catch((error) => {
      setIsLoading(false);
      setError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section className={classes.mealsLoading}>
        <p>Loading...</p>
      </section>
    );
  }

  if (error) {
    return (
      <section className={classes.mealsLoading}>
        <p>
          {Swal.fire({
            icon: "error",
            title: "Oops...",
            text: error,    
          })}
        </p>

      </section>
    );
  }

  const mealsList = loadedOrders.map((meal) => (
    <MealItem
    //   id={meal.id}
    //   key={meal.id}
    //   name={meal.name}
    //   description={meal.description}
    //   price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
        <button className={classes.button} onClick={props.onGoBack}>Atr√°s</button>
      </Card>
    </section>
  );
};

export default Orders;
