import React, { useEffect, useState } from "react";
import Swal from "sweetalert2";

import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItem";
import Card from "../UI/Card";

const AvailableMeals = (props) => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [orderType, setOrderType] = useState('Bebidas');



  useEffect(() => {

    const fetchMeals = async () => {

      const response = await fetch(
        `https://menu-app-d307a-default-rtdb.firebaseio.com/Empanadas.json`
      );

      const parrillada = await fetch(
        `https://menu-app-d307a-default-rtdb.firebaseio.com/Parrillada.json`
      );

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      props.onType

      const responseData = await response.json();

      const loadedMeals = [];

      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }
      setMeals(loadedMeals);
      setIsLoading(false);
    };

    fetchMeals().catch((error) => {
      setIsLoading(false);
      setError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section className={classes.mealsLoading}>
        <p>Loading...</p>
      </section>
    );
  }

  if (error) {
    const buttonBack = (
      <button className={classes.button} onClick={props.onGoBack}>
        Atrás
      </button>
    );
    return (
      <div className={classes.errorContainer}>
        <Card className={classes.mealsLoading}>
          <p>{error}</p>
          {buttonBack}
        </Card>
      </div>
    );
  }

  const mealsList = meals.map((meal) => (
    <MealItem
      id={meal.id}
      key={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
        <button className={classes.button} onClick={props.onGoBack}>
          Atrás
        </button>
      </Card>
    </section>
  );
};

export default AvailableMeals;
