import React, { useState, useRef } from "react";
import CartProvider from "../../store/CartProvider";
import Orders from "./components/Orders";


const Admin = () => {
  const [admission, setAdmission] = useState(true);

  const usernameInputRef = useRef();

  const submitHandler = (event) => {
    event.preventDefault();
    console.log(admission);

    // const enteredUsername = usernameInputRef.current.value;

    // if(enteredUsername === 'avestruz'){
    //   setAdmission(false)
    //   return;
    // } else {
    //   alert('Wrong password.')
    // }
  };

  const toggle = (event) => {
    console.log(admission)
    setAdmission(!admission)
    console.log(admission)
  }

  const orderPassword = 
    <form onSubmit={submitHandler}>
      <label htmlFor="username" placeholder="Username"></label>
      <input ref={usernameInputRef} type='text' name="username"/>
      <label htmlFor="password" placeholder="Password"></label>
      <input type='password' name="password"/>
      <button onClick={toggle} type="button">Enviar</button>
    </form>

  return (
    <CartProvider>
      {orderPassword}
      {orderPassword === true ? <Orders /> : orderPassword}
    </CartProvider>
  );
};

export default Admin;
