import React, { useState } from "react";
import classes from './CategoriesMeals.module.css'




const empanadas = 'https://menu-app-d307a-default-rtdb.firebaseio.com/Empanadas.json';
const ensaladas = 'https://menu-app-d307a-default-rtdb.firebaseio.com/Ensaladas.json';
const bebidas = 'https://menu-app-d307a-default-rtdb.firebaseio.com/Bebidas.json';
const parrilada = 'https://menu-app-d307a-default-rtdb.firebaseio.com/Parrilada.json';


const CategoriesMeals = (props) => {
  const [type, setType] = useState(0);

  {meals === 1 && <AvailableMeals onGoBack={addGoBackHandler}/>}
      {meals === 2 && <Parrillada onGoBack={addGoBackHandler} />}
      {meals === 3 && <Ensalada onGoBack={addGoBackHandler} />}
      {meals === 4 && <Bebidas onGoBack={addGoBackHandler} />} 

  return (
    <div className={classes.container}>
        <div onClick={props.onAddMeal && setType(1)}  className={classes.card}>
          <h2>Empanadas</h2>
        </div>
        <div onClick={props.onAddParrilla} order={parrilada} className={classes.card}>
          <h2>Parrillada</h2>
        </div>
        <div onClick={props.onAddEnsalada}  className={classes.card}>
          <h2>Ensaladas</h2>
        </div>
        <div onClick={props.onAddBebidas} className={classes.card}>
          <h2>Bebidas</h2>
        </div>
    </div>
  );
};

export default CategoriesMeals;
