import React, { useEffect, useState, useContext } from "react";
import Swal from "sweetalert2";

import classes from "./Orders.module.css";
import Card from "../../UI/Card";

const Orders = (props) => {
  const [order, setOrder] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      const response = await fetch(
        "https://menu-app-d307a-default-rtdb.firebaseio.com/orders.json"
      );

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseData = await response.json();

      const loadedOrders = [];

      for (const key in responseData) {
        // console.log(responseData[key].orderedItems)
        loadedOrders.push({
          id: key,
          orderedItems: responseData[key].orderedItems,
          total: responseData[key].total,
          user: responseData[key].user,
        });
      }
      setOrder(loadedOrders);
      setIsLoading(false);
      console.log(loadedOrders);
    };

    fetchOrders().catch((error) => {
      setIsLoading(false);
      setError(error.message);
    });
  }, []);

  if (isLoading) {
    return (
      <section className={classes.mealsLoading}>
        <p>Loading...</p>
      </section>
    );
  }

  if (error) {
    return (
      <section className={classes.mealsLoading}>
        <p>
          {Swal.fire({
            icon: "error",
            title: "Oops...",
            text: error,
          })}
        </p>
      </section>
    );
  }


  const mealsList = order.reverse().map((orders) => (
    <li className={classes.order}>
      <div>
        <h3>{props.name}</h3>
        <div className={classes.description}>{props.description}</div>
        <div className={classes.price}>{price}</div>
      </div>
      {orders.orderedItems.map((item) => (
        <p>
          {item.amount} -- {item.name} -- {item.amount * item.price}
          <br />
          {item.amount * item.price}
        </p>
      ))}
      <span>Total: {orders.total}</span>
    </li>
  ));

  return (
    <section className={classes.meals}>
        <Card>{mealsList}</Card>
        <button className={classes.button} onClick={props.onGoBack}>
          Atr√°s
        </button>
    </section>
  );
};

export default Orders;
