import React, { useState, useRef } from "react";
import CartProvider from "../../store/CartProvider";
import Orders from "./components/Orders";


const Admin = () => {
  const [admission, setAdmission] = useState(false);

  const usernameInputRef = useRef();
  const passwordInputRef = useRef();

  const submitHandler = (event) => {
    event.preventDefault();
    console.log(admission);

    const enteredUsername = usernameInputRef.current.value;
    const enteredPassword = usernameInputRef.current.value;

    if(enteredUsername === 'admin'){
      setAdmission(true)
      return;
    } else {
      alert('Wrong password.')
    }
  };

  const orderPassword = 
    <form onSubmit={submitHandler}>
      <label htmlFor="username" placeholder="Username"></label>
      <input ref={usernameInputRef} type='text' name="username"/>
      <label htmlFor="password" placeholder="Password"></label>
      <input type='password' name="password"/>
      <button type="submit">Enviar</button>
    </form>

  return (
    <CartProvider>
      {admission === true ? <Orders /> : orderPassword}
    </CartProvider>
  );
};

export default Admin;
